overlays/modal-triggered-by-menu-demo.raw:
'use client'

import { Button, buttonVariants, Menu, Modal } from '@/components/ui'
import { wait } from '@/lib/utils'
import { Loader2, MoreVertical } from 'lucide-react'
import React from 'react'

export default function ModalTriggeredByMenuDemo() {
    const [state, setState] = React.useState<string | null>(null)
    const [loading, setLoading] = React.useState<boolean>(false)
    const closeModal = () => setState(null)
    const executeAction = (action: string) => {
        console.log(`${action} is executing`)
        setLoading(true)
        wait(2000).then(() => {
            setLoading(false)
            closeModal()
        })
    }

    const actionType = (t: string | null) => {
        switch (t) {
            case 'delete':
                return {
                    title: 'Delete User',
                    description: 'Are you sure you want to delete this item?',
                    confirmText: 'Delete',
                    action: () => executeAction(t)
                }

            case 'ban':
                return {
                    title: 'Ban User',
                    description: 'Are you sure you want to ban this user?',
                    confirmText: 'Ban',
                    action: () => executeAction(t)
                }

            case 'restore':
                return {
                    title: 'Restore User',
                    description: 'Are you sure you want to restore this user?',
                    confirmText: 'Restore',
                    action: () => executeAction(t)
                }
            default:
                return
        }
    }
    return (
        <>
            <Menu>
                <Menu.Trigger className={buttonVariants({ variant: 'outline', size: 'icon' })}>
                    <MoreVertical />
                </Menu.Trigger>
                <Menu.Content placement='bottom'>
                    <Menu.Item isDanger onAction={() => setState('delete')}>
                        Delete
                    </Menu.Item>
                    <Menu.Item onAction={() => setState('ban')}>Ban</Menu.Item>
                    <Menu.Item onAction={() => setState('restore')}>Restore</Menu.Item>
                </Menu.Content>
            </Menu>

            <Modal.Overlay isOpen={state !== null} onOpenChange={closeModal}>
                <Modal.Content>
                    <Modal.Header>
                        <Modal.Title>{actionType(state)?.title}</Modal.Title>
                        <Modal.Description>{actionType(state)?.description}</Modal.Description>
                    </Modal.Header>
                    <Modal.Footer>
                        <Modal.Close>Cancel</Modal.Close>
                        <Button
                            className='min-w-24'
                            isDisabled={loading}
                            onPress={actionType(state)?.action}
                            variant={actionType(state)?.confirmText === 'Restore' ? 'primary' : 'danger'}
                        >
                            {loading ? <Loader2 className={'animate-spin'} /> : actionType(state)?.confirmText}
                        </Button>
                    </Modal.Footer>
                </Modal.Content>
            </Modal.Overlay>
        </>
    )
}

overlays/sheet-demo.raw:
'use client'

import { OptionPreview } from '@/components/docs/outside/option-preview'
import { Button, Sheet, Switch } from '@/components/ui'
import React, { useState } from 'react'
import { titleCase } from 'usemods'

type Side = 'left' | 'right' | 'top' | 'bottom'
export default function SheetDemo() {
    const [sheetSide, setSheetSide] = React.useState<Side>('left')
    const [isOpen, setIsOpen] = React.useState(false)

    const [settings, setSettings] = useState({
        closeButton: true,
        isStack: true
    })

    const sides: Side[] = ['left', 'right', 'top', 'bottom']

    const pressHandler = (side: Side, open: boolean) => {
        setSheetSide(side)
        setIsOpen(open)
    }

    return (
        <>
            <OptionPreview>
                <Switch
                    defaultSelected={settings.isStack}
                    onChange={() => setSettings({ ...settings, isStack: !settings.isStack })}
                >
                    Toggle stack
                </Switch>
                <Switch
                    defaultSelected={settings.closeButton}
                    onChange={() => setSettings({ ...settings, closeButton: !settings.closeButton })}
                >
                    With x button
                </Switch>
            </OptionPreview>
            <div className='grid grid-cols-2 gap-2'>
                {sides.map((side, idx) => (
                    <Button variant='outline' onPress={() => pressHandler(side, true)} key={idx}>
                        {titleCase(side)}
                    </Button>
                ))}
            </div>
            <Sheet.Overlay isOpen={isOpen} onOpenChange={setIsOpen}>
                <Sheet.Content closeButton={settings.closeButton} isStack={settings.isStack} side={sheetSide}>
                    <Sheet.Header>
                        <Sheet.Title>{titleCase(sheetSide)}</Sheet.Title>
                        <Sheet.Description>The sheet will go from {sheetSide} side.</Sheet.Description>
                    </Sheet.Header>
                </Sheet.Content>
            </Sheet.Overlay>
        </>
    )
}

