'use client'

import React from 'react'
import * as Primitive from 'react-aria-components'
import { tv } from 'tailwind-variants'
import { Modal } from './modal'

export interface PopoverProps extends Omit<Primitive.PopoverProps, 'children'> {
    showArrow?: boolean
    children: React.ReactNode
}

const styles = tv({
    base: 'max-w-xs rounded-xl border bg-background bg-clip-padding p-4 text-foreground shadow-lg dark:backdrop-blur-2xl dark:backdrop-saturate-200 sm:max-w-3xl',
    variants: {
        isEntering: {
            true: 'duration-200 ease-out animate-in fade-in placement-left:slide-in-from-right-1 placement-right:slide-in-from-left-1 placement-top:slide-in-from-bottom-1 placement-bottom:slide-in-from-top-1'
        },
        isExiting: {
            true: 'duration-150 ease-in animate-out fade-out placement-left:slide-out-to-right-1 placement-right:slide-out-to-left-1 placement-top:slide-out-to-bottom-1 placement-bottom:slide-out-to-top-1'
        }
    }
})

interface PopoverSubComponents {
    Content: typeof PopoverContent
    Trigger: typeof PopoverTrigger
    Close: typeof PopoverClose
    Overlay: typeof PopoverOverlay
    Footer: typeof PopoverFooter
    Header: typeof PopoverHeader
    Title: typeof PopoverTitle
    Description: typeof PopoverDescription
    Body: typeof PopoverBody
    Picker: typeof PopoverPicker
}

type PopoverComponent = React.FC<Primitive.DialogTriggerProps> & PopoverSubComponents

const Popover: PopoverComponent = (props: Primitive.DialogTriggerProps) => <Primitive.DialogTrigger {...props} />
const PopoverTrigger = (props: Primitive.ButtonProps) => <Primitive.Button {...props} />
const PopoverClose = Modal.Close
const PopoverOverlay = Modal.Overlay
const PopoverFooter = Modal.Footer
const PopoverHeader = Modal.Header
const PopoverTitle = Modal.Title
const PopoverDescription = Modal.Description
const PopoverBody = Modal.Body

const PopoverContent = ({ children, showArrow = true, className, ...props }: PopoverProps) => {
    const popoverContext = Primitive.useSlottedContext(Primitive.PopoverContext)!
    const isSubmenu = popoverContext?.trigger === 'SubmenuTrigger'
    let offset = showArrow ? 12 : 8
    offset = isSubmenu ? offset - 6 : offset
    return (
        <Primitive.Popover
            offset={offset}
            {...props}
            className={Primitive.composeRenderProps(className, (className, renderProps) =>
                styles({ ...renderProps, className })
            )}
        >
            {showArrow && (
                <Primitive.OverlayArrow className='group'>
                    <svg
                        width={12}
                        height={12}
                        viewBox='0 0 12 12'
                        className='block fill-background stroke-border group-placement-left:-rotate-90 group-placement-right:rotate-90 group-placement-bottom:rotate-180'
                    >
                        <path d='M0 0 L6 6 L12 0' />
                    </svg>
                </Primitive.OverlayArrow>
            )}
            {children}
        </Primitive.Popover>
    )
}

const PopoverPicker = ({ showArrow = false, children, ...props }: PopoverProps) => {
    return (
        <PopoverContent showArrow={showArrow} className='max-h-72 min-w-[--trigger-width] p-0' {...props}>
            {children}
        </PopoverContent>
    )
}

Popover.Body = PopoverBody
Popover.Close = PopoverClose
Popover.Content = PopoverContent
Popover.Description = PopoverDescription
Popover.Footer = PopoverFooter
Popover.Header = PopoverHeader
Popover.Overlay = PopoverOverlay
Popover.Picker = PopoverPicker
Popover.Title = PopoverTitle
Popover.Trigger = PopoverTrigger

export { Popover }

multi-select.raw:
'use client'

import * as React from 'react'

import { cn } from '@/lib/utils'
import * as Primitive from 'cmdk'
import { Badge } from './badge'
import { Description, FieldError, Label } from './field'

interface ItemProps {
    label: string
    value: string
}

interface Props {
    items: ItemProps[]
    placeholder?: string
    selected: ItemProps[]
    setSelected: React.Dispatch<React.SetStateAction<ItemProps[]>>
    max?: number
    className?: string
    description?: string
    errorMessage?: string
    label: string
}

export function MultiSelect({
    label,
    description,
    errorMessage,
    items,
    placeholder = 'Select items...',
    max = 5,
    selected,
    setSelected,
    className
}: Props) {
    const inputRef = React.useRef<HTMLInputElement>(null)
    const [open, setOpen] = React.useState(false)
    const [inputValue, setInputValue] = React.useState('')
    const handleUnselect = React.useCallback(
        (item: ItemProps) => {
            setSelected((prev) => prev.filter((s) => s.value !== item.value))
        },
        [setSelected]
    )

    const handleKeyDown = React.useCallback(
        (e: React.KeyboardEvent<HTMLDivElement>) => {
            const input = inputRef.current
            if (input) {
                if (e.key === 'Delete' || e.key === 'Backspace') {
                    if (input.value === '') {
                        setSelected((prev) => {
                            const newSelected = [...prev]
                            newSelected.pop()
                            return newSelected
                        })
                    }
                }

                if (e.key === 'Escape') {
                    input.blur()
                }
            }
        },
        [setSelected]
    )
    const selectables = items.filter((item) => !selected.includes(item))
    React.useEffect(() => {
        if (selected.length >= max) {
            setOpen(false)
        }
    }, [selected, max])

    const id = React.useId()
    return (
        <div className={cn('group flex w-full flex-col gap-1', className)}>
            <Label onClick={() => inputRef.current?.focus()}>{label}</Label>
            <Primitive.Command onKeyDown={handleKeyDown} className='overflow-visible bg-transparent'>
                <div className='group rounded-lg border border-input bg-background px-2 py-[0.55rem] text-sm transition duration-200 focus-within:border-primary/70 focus-within:ring-[0.20rem] focus-within:ring-primary/20 focus-within:ring-offset-0'>
                    <div className='flex flex-wrap gap-1'>
                        {selected.map((item, index) => {
                            return (
                                <Badge key={index} className='inline-flex items-center rounded-sm px-1'>
                                    <span className='text-xs'>{item.label}</span>
                                    <button
                                        aria-label={`Remove ${item.label} option`}
                                        aria-roledescription='button to remove option'
                                        type='button'
                                        className='inline-grid size-4 place-content-center rounded-full border border-transparent focus:border-foreground focus:text-foreground focus:outline-none'
                                        onKeyDown={(e) => {
                                            if (e.key === 'Enter') {
                                                handleUnselect(item)
                                            }
                                        }}
                                        onMouseDown={(e) => {
                                            e.preventDefault()
                                            e.stopPropagation()
                                        }}
                                        onClick={() => handleUnselect(item)}
                                    >
                                        <span className='sr-only'>Remove {item.label} option</span>
                                        <svg
                                            className='size-3.5 hover:text-foreground'
                                            xmlns='http://www.w3.org/2000/svg'
                                            viewBox='0 0 24 24'
                                        >
                                            <g
                                                fill='none'
                                                stroke='currentColor'
                                                strokeLinecap='round'
                                                strokeLinejoin='round'
                                                strokeWidth={2}
                                            >
                                                <circle cx={12} cy={12} r={10}></circle>
                                                <path d='m15 9l-6 6m0-6l6 6'></path>
                                            </g>
                                        </svg>
                                    </button>
                                </Badge>
                            )
                        })}

                        <Primitive.CommandInput
                            aria-labelledby='multi-select-label'
                            ref={inputRef}
                            value={inputValue}
                            onValueChange={setInputValue}
                            onBlur={() => setOpen(false)}
                            onFocus={() => {
                                if (selected.length < max) {
                                    setOpen(true)
                                }
                            }}
                            placeholder={selected.length >= max ? 'Remove one to select more' : placeholder}
                            className='ml-0.5 flex-1 bg-transparent outline-none placeholder:text-muted-foreground'
                        />
                    </div>
                </div>
                <div className='relative'>
                    <Primitive.CommandList>
                        {open && selectables.length > 0 ? (
                            <div className='absolute top-0 z-10 mt-2 max-h-72 w-full overflow-y-auto overflow-x-hidden rounded-lg border bg-background p-1 text-foreground shadow-md outline-none animate-in'>
                                <Primitive.CommandGroup className='h-full overflow-auto p-0'>
                                    {selectables.map((item) => (
                                        <Primitive.CommandItem
                                            disabled={selected.includes(item)}
                                            key={item.value}
                                            onMouseDown={(e) => {
                                                e.preventDefault()
                                                e.stopPropagation()
                                            }}
                                            onSelect={() => {
                                                setInputValue('')
                                                setSelected((prev) => [...prev, item])
                                            }}
                                            className='relative flex w-full cursor-pointer justify-between rounded-md p-2 text-sm transition-colors data-[selected=true]:bg-primary data-[selected=true]:text-primary-foreground'
                                        >
                                            {item.label}
                                        </Primitive.CommandItem>
                                    ))}
                                </Primitive.CommandGroup>
                            </div>
                        ) : null}
                    </Primitive.CommandList>
                </div>
            </Primitive.Command>
            {description && <Description>{description}</Description>}
            <FieldError>{errorMessage}</FieldError>
        </div>
    )
}

