carousel.raw:
'use client'

import useEmblaCarousel, { type UseEmblaCarouselType } from 'embla-carousel-react'
import * as React from 'react'

import { cn } from '@/lib/utils'
import { ChevronLeft, ChevronRight } from 'lucide-react'
import { Button, ButtonProps } from './button'

type CarouselApi = UseEmblaCarouselType[1]
type UseCarouselParameters = Parameters<typeof useEmblaCarousel>
type CarouselOptions = UseCarouselParameters[0]
type CarouselPlugin = UseCarouselParameters[1]

type CarouselProps = {
    opts?: CarouselOptions
    plugins?: CarouselPlugin
    orientation?: 'horizontal' | 'vertical'
    setApi?: (api: CarouselApi) => void
}

type CarouselContextProps = {
    carouselRef: ReturnType<typeof useEmblaCarousel>[0]
    api: ReturnType<typeof useEmblaCarousel>[1]
    scrollPrev: () => void
    scrollNext: () => void
    canScrollPrev: boolean
    canScrollNext: boolean
} & CarouselProps

const CarouselContext = React.createContext<CarouselContextProps | null>(null)

function useCarousel() {
    const context = React.useContext(CarouselContext)

    if (!context) {
        throw new Error('useCarousel must be used within a <Carousel />')
    }

    return context
}

interface CarouselSubComponents {
    Content: typeof CarouselContent
    Item: typeof CarouselItem
    Previous: typeof CarouselPrevious
    Next: typeof CarouselNext
}

type CarouselComponent = React.ForwardRefExoticComponent<React.HTMLAttributes<HTMLDivElement>> &
    React.RefAttributes<HTMLDivElement> &
    CarouselSubComponents

const Carousel: CarouselComponent = React.forwardRef<
    HTMLDivElement,
    React.HTMLAttributes<HTMLDivElement> & CarouselProps
>(({ orientation = 'horizontal', opts, setApi, plugins, className, children, ...props }, ref) => {
    const [carouselRef, api] = useEmblaCarousel(
        {
            ...opts,
            axis: orientation === 'horizontal' ? 'x' : 'y'
        },
        plugins
    )
    const [canScrollPrev, setCanScrollPrev] = React.useState(false)
    const [canScrollNext, setCanScrollNext] = React.useState(false)

    const onSelect = React.useCallback((api: CarouselApi) => {
        if (!api) {
            return
        }

        setCanScrollPrev(api.canScrollPrev())
        setCanScrollNext(api.canScrollNext())
    }, [])

    const scrollPrev = React.useCallback(() => {
        api?.scrollPrev()
    }, [api])

    const scrollNext = React.useCallback(() => {
        api?.scrollNext()
    }, [api])

    const handleKeyDown = React.useCallback(
        (event: React.KeyboardEvent<HTMLDivElement>) => {
            if (event.key === 'ArrowLeft') {
                event.preventDefault()
                scrollPrev()
            } else if (event.key === 'ArrowRight') {
                event.preventDefault()
                scrollNext()
            }
        },
        [scrollPrev, scrollNext]
    )

    React.useEffect(() => {
        if (!api || !setApi) {
            return
        }

        setApi(api)
    }, [api, setApi])

    React.useEffect(() => {
        if (!api) {
            return
        }

        onSelect(api)
        api.on('reInit', onSelect)
        api.on('select', onSelect)

        return () => {
            api?.off('select', onSelect)
        }
    }, [api, onSelect])

    return (
        <CarouselContext.Provider
            value={{
                carouselRef,
                api: api,
                opts,
                orientation: orientation || (opts?.axis === 'y' ? 'vertical' : 'horizontal'),
                scrollPrev,
                scrollNext,
                canScrollPrev,
                canScrollNext
            }}
        >
            <div
                ref={ref}
                onKeyDownCapture={handleKeyDown}
                className={cn('relative', className)}
                role='region'
                aria-roledescription='carousel'
                {...props}
            >
                {children}
            </div>
        </CarouselContext.Provider>
    )
}) as CarouselComponent
Carousel.displayName = 'Carousel'

const CarouselContent = React.forwardRef<HTMLDivElement, React.HTMLAttributes<HTMLDivElement>>(
    ({ className, ...props }, ref) => {
        const { carouselRef, orientation } = useCarousel()

        return (
            <div ref={carouselRef} className='overflow-hidden'>
                <div
                    ref={ref}
                    className={cn('flex', orientation === 'horizontal' ? '-ml-4' : '-mt-4 flex-col', className)}
                    {...props}
                />
            </div>
        )
    }
)
CarouselContent.displayName = 'CarouselContent'

const CarouselItem = React.forwardRef<HTMLDivElement, React.HTMLAttributes<HTMLDivElement>>(
    ({ className, ...props }, ref) => {
        const { orientation } = useCarousel()

        return (
            <div
                ref={ref}
                role='group'
                aria-roledescription='slide'
                className={cn(
                    'min-w-0 shrink-0 grow-0 basis-full',
                    orientation === 'horizontal' ? 'pl-4' : 'pt-4',
                    className
                )}
                {...props}
            />
        )
    }
)
CarouselItem.displayName = 'CarouselItem'

const CarouselPrevious = ({ className, variant = 'outline', size = 'icon', ...props }: ButtonProps) => {
    const { orientation, scrollPrev, canScrollPrev } = useCarousel()

    return (
        <Button
            variant={variant}
            size={size}
            className={cn(
                'absolute h-8 w-8 rounded-full',
                orientation === 'horizontal'
                    ? '-left-12 top-1/2 -translate-y-1/2'
                    : '-top-12 left-1/2 -translate-x-1/2 rotate-90',
                className
            )}
            isDisabled={!canScrollPrev}
            onPress={scrollPrev}
            {...props}
        >
            <ChevronLeft className='h-4 w-4' />
            <span className='sr-only'>Previous slide</span>
        </Button>
    )
}
CarouselPrevious.displayName = 'CarouselPrevious'

const CarouselNext = ({ className, variant = 'outline', size = 'icon', ...props }: ButtonProps) => {
    const { orientation, scrollNext, canScrollNext } = useCarousel()

    return (
        <Button
            variant={variant}
            size={size}
            className={cn(
                'absolute h-8 w-8 rounded-full',
                orientation === 'horizontal'
                    ? '-right-12 top-1/2 -translate-y-1/2'
                    : '-bottom-12 left-1/2 -translate-x-1/2 rotate-90',
                className
            )}
            isDisabled={!canScrollNext}
            onPress={scrollNext}
            {...props}
        >
            <ChevronRight className='h-4 w-4' />
            <span className='sr-only'>Next slide</span>
        </Button>
    )
}
CarouselNext.displayName = 'CarouselNext'

Carousel.Content = CarouselContent
Carousel.Item = CarouselItem
Carousel.Previous = CarouselPrevious
Carousel.Next = CarouselNext

export { Carousel }

menu.raw:
'use client'

import { cn } from '@/lib/utils'
import { Check, ChevronsRight } from 'lucide-react'
import * as React from 'react'
import * as Primitive from 'react-aria-components'
import { VariantProps } from 'tailwind-variants'
import { dropdownItemStyles } from './dropdown'

interface MenuSubComponents {
    CheckboxItem: typeof MenuCheckboxItem
    Content: typeof MenuContent
    Header: typeof MenuHeader
    Item: typeof MenuItem
    Keyboard: typeof MenuKeyboard
    RadioItem: typeof MenuRadioItem
    Section: typeof MenuSection
    Separator: typeof MenuSeparator
    Trigger: typeof MenuTrigger
    SubTrigger: typeof SubmenuTrigger
}

type MenuComponent = React.FC<Primitive.MenuTriggerProps> & MenuSubComponents

const Menu: MenuComponent = (props: Primitive.MenuTriggerProps) => (
    <Primitive.MenuTrigger {...props}>{props.children}</Primitive.MenuTrigger>
)
const MenuTrigger = ({ className, ...props }: Primitive.ButtonProps) => (
    <Primitive.Button
        aria-label='Open Menu'
        className={cn(
            'inline text-left focus:outline-none focus-visible:ring-1 focus-visible:ring-primary/20 pressed:outline-none',
            className
        )}
        {...props}
    />
)

const SubmenuTrigger = Primitive.SubmenuTrigger

const MenuSection = Primitive.Section

export interface MenuContentProps<T>
    extends Omit<Primitive.PopoverProps, 'children' | 'style'>,
        Primitive.MenuProps<T> {
    className?: string
    popoverClassName?: string
    showArrow?: boolean
}

const MenuContent = <T extends object>({
    className,
    showArrow = false,
    popoverClassName,
    offset = 4,
    ...props
}: MenuContentProps<T>) => {
    let popoverContext = Primitive.useSlottedContext(Primitive.PopoverContext)!
    let isSubmenu = popoverContext?.trigger === 'SubmenuTrigger'
    let currentOffset = showArrow ? 12 : 8
    currentOffset = isSubmenu ? currentOffset - 6 : currentOffset
    return (
        <Primitive.Popover
            offset={currentOffset}
            className={cn(
                'z-50 min-w-40 rounded-xl border bg-background text-foreground outline-none data-[placement=bottom]:slide-in-from-top-2 data-[placement=left]:slide-in-from-right-2 data-[placement=right]:slide-in-from-left-2 data-[placement=top]:slide-in-from-bottom-2 entering:animate-in entering:fade-in-0 exiting:animate-out exiting:fade-out-0 exiting:zoom-out-95',
                popoverClassName
            )}
            {...props}
        >
            {showArrow && (
                <Primitive.OverlayArrow className='group'>
                    <svg
                        width={12}
                        height={12}
                        viewBox='0 0 12 12'
                        className='block fill-popover stroke-border group-placement-left:-rotate-90 group-placement-right:rotate-90 group-placement-bottom:rotate-180'
                    >
                        <path d='M0 0 L6 6 L12 0' />
                    </svg>
                </Primitive.OverlayArrow>
            )}
            <Primitive.Menu
                className={cn(
                    'z32kk',
                    'max-h-[inherit] overflow-auto rounded-xl p-1 outline outline-0 [clip-path:inset(0_0_0_0_round_calc(var(--radius)-2px))]',
                    className
                )}
                {...props}
            />
        </Primitive.Popover>
    )
}

interface MenuItemProps extends Omit<Primitive.MenuItemProps, 'isDanger'>, VariantProps<typeof dropdownItemStyles> {
    inset?: boolean
    isDanger?: boolean
}

const MenuItem = ({ className, isDanger = false, inset, children, ...props }: MenuItemProps) => (
    <Primitive.MenuItem
        className={Primitive.composeRenderProps(className, (className, renderProps) =>
            dropdownItemStyles({
                ...renderProps,
                className: cn(inset && 'pl-8', className)
            })
        )}
        data-danger={isDanger ? 'true' : undefined}
        {...props}
    >
        {/*<MenuItemPrimitive className={cn(menuItemVariants({ intent }), className, inset && 'pl-8')} {...props}>*/}
        {(values) => (
            <>
                {typeof children === 'function' ? children(values) : children}
                {values.hasSubmenu && <ChevronsRight className='gpfw ml-auto size-3.5' />}
            </>
        )}
    </Primitive.MenuItem>
)

export interface MenuHeaderProps extends React.ComponentProps<typeof Primitive.Header> {
    inset?: boolean
    separator?: boolean
}

const MenuHeader = ({ className, inset, separator = false, ...props }: MenuHeaderProps) => (
    <Primitive.Header
        className={cn(
            'px-2 py-1.5 text-base font-semibold sm:text-sm',
            inset && 'pl-8',
            separator && '-mx-1 mb-1 border-b border-b-border px-3 pb-[0.625rem]',
            className
        )}
        {...props}
    />
)

const MenuSeparator = ({ className, ...props }: Primitive.SeparatorProps) => (
    <Primitive.Separator className={cn('-mx-1 my-1 h-px bg-muted', className)} {...props} />
)

interface MenuKeyboardProps extends React.HTMLAttributes<HTMLElement> {
    keys: string | string[]
}

const MenuKeyboard = ({ keys, className, ...props }: MenuKeyboardProps) => {
    return (
        <Primitive.Keyboard
            className={cn('-mr-1 ml-auto hidden items-center gap-[0.170rem] px-1 lg:inline-flex', className)}
            {...props}
        >
            {(Array.isArray(keys) ? keys : keys.split('')).map((char, index) => (
                <kbd
                    key={index}
                    className={cn([
